$("a[href='#top']").click(function(e) {
	e.preventDefault();
	$("html, body").animate({ scrollTop: 0 }, "slow");
	return false;
});

$('a[href^="#"]').click(function(e) {
    e.preventDefault();
    var target = this.hash;
    $target = $(target);
    $('html, body').stop().animate({
        'scrollTop': $target.offset().top
    }, 700, 'swing', function() { 
		window.location.hash = target; 
		return false;	
	});
});